import React, { useState, useEffect } from 'react';

const App = () => {
    const [walletAddress, setWalletAddress] = useState('');
    const [balance, setBalance] = useState(null);
    const [conversionRate, setConversionRate] = useState(null);
    const [billAmount, setBillAmount] = useState('');
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchConversionRate = async () => {
            try {
                const response = await fetch('https://api.coingecko.com/api/v3/simple/price?ids=cardano&vs_currencies=usd');
                const data = await response.json();
                setConversionRate(data.cardano.usd);
            } catch (error) {
                console.error('Error fetching conversion rate:', error);
                setError('Error fetching conversion rate');
            }
        };

        fetchConversionRate();
    }, []);

    const fetchBalance = async () => {
        try {
            setError(null);
            const response = await fetch(`http://localhost:4000/api/ada/balance/${walletAddress}`);
            const data = await response.json();
            setBalance(data.balance);
        } catch (error) {
            console.error('Error fetching balance:', error);
            setError('Error fetching balance');
        }
    };

    const handleBillAmountChange = (e) => {
        setBillAmount(e.target.value);
    };

    const calculateAdaAmount = () => {
        if (!conversionRate || !billAmount) return 0;
        return (parseFloat(billAmount) / conversionRate).toFixed(4);
    };

    const totalPayableAmount = () => {
        const adaAmount = parseFloat(calculateAdaAmount());
        return (adaAmount + 5).toFixed(2);
    };

    const connectMetaMask = async () => {
        if (window.ethereum) {
            try {
                const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
                console.log('Connected MetaMask accounts:', accounts);
                setWalletAddress(accounts[0]);
            } catch (error) {
                console.error('Error connecting to MetaMask:', error);
                setError('Error connecting to MetaMask');
            }
        } else {
            alert('MetaMask is not installed. Please install MetaMask and try again.');
        }
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div style={styles.container}>
            <h1>USD - ADA - USD</h1>
            <h2>USD to ADA: 1$ : {conversionRate ? `${(1 / conversionRate).toFixed(4)} ADA` : 'Fetching...'}</h2>
            <h2>ADA to USD: 1 ADA : {conversionRate ? `${conversionRate.toFixed(4)} $` : 'Fetching...'}</h2>

            <div style={styles.billInputContainer}>
                <h1>Input Bill Amount</h1>
                <input 
                    type='text' 
                    placeholder='Enter your bill in USD' 
                    value={billAmount}
                    onChange={handleBillAmountChange}
                    style={styles.input}
                    required 
                />
            </div>

            <h3>Bill Amount in ADA: {calculateAdaAmount()} ADA</h3>
            <h4>Payment Processing ADA Blocked: 5 ADA</h4>
            <h3>Total Payable Amount: {calculateAdaAmount()} ADA + 5 ADA = {totalPayableAmount()} ADA</h3>

            <div style={styles.buttonContainer}>
                <button style={styles.button} onClick={openModal}>Approve Payment</button>
                <button style={styles.button}>Reject Payment</button>
            </div>

            {isModalOpen && (
                <div style={styles.modal}>
                    <div style={styles.modalContent}>
                        <span style={styles.close} onClick={closeModal}>&times;</span>
                        <h3>Next Page</h3>
                        <div style={styles.walletInputContainer}>
                            <input
                                type="text"
                                value={walletAddress}
                                onChange={(e) => setWalletAddress(e.target.value)}
                                placeholder="Enter Wallet Address to connect eternal wallet"
                                style={styles.input}
                            />
                            <button onClick={fetchBalance} style={styles.button}>Connect</button>
                        </div>
                        {balance !== null && <p>Balance: {balance} ADA</p>}
                        {error && <p style={styles.error}>{error}</p>}
                        <h3>Wallet Balance: {balance !== null ? balance : 'Fetching...'} ADA</h3>
                        <button style={styles.button} onClick={connectMetaMask}>Pay {calculateAdaAmount()} ADA</button>
                    </div>
                </div>
            )}
        </div>
    );
};

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
    },
    billInputContainer: {
        margin: '20px 0',
    },
    walletInputContainer: {
        margin: '20px 0',
        display: 'flex',
        alignItems: 'center',
    },
    input: {
        padding: '10px',
        margin: '10px 0',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '300px',
    },
    buttonContainer: {
        display: 'flex',
        gap: '10px',
    },
    button: {
        padding: '10px 20px',
        margin: '10px 0',
        borderRadius: '5px',
        border: 'none',
        backgroundColor: '#007bff',
        color: '#fff',
        cursor: 'pointer',
    },
    error: {
        color: 'red',
    },
    modal: {
        position: 'fixed',
        top: '0',
        left: '0',
        width: '100%',
        height: '100%',
        backgroundColor: 'rgba(0,0,0,0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    modalContent: {
        backgroundColor: '#fff',
        padding: '20px',
        borderRadius: '5px',
        width: '500px',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    close: {
        position: 'absolute',
        top: '10px',
        right: '10px',
        fontSize: '20px',
        cursor: 'pointer',
    }
};

export default App;
