import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
    const [walletAddress, setWalletAddress] = useState('');
    const [balance, setBalance] = useState(null);
    const [conversionRate, setConversionRate] = useState(null);
    const [billAmount, setBillAmount] = useState('');
    const [error, setError] = useState(null);

    const fetchBalance = async () => {
        try {
            setError(null);
            const response = await axios.get(`http://localhost:4000/api/ada/balance/${walletAddress}`);
            setBalance(response.data.balance);
        } catch (error) {
            console.error('Error fetching balance:', error);
            setError(error.response?.data?.message || 'Error fetching balance');
        }
    };

    const fetchConversionRate = async () => {
        try {
            setError(null);
            const response = await axios.get('http://localhost:4000/api/ada/convert');
            setConversionRate(response.data.adaToUsdRate);
        } catch (error) {
            console.error('Error fetching conversion rate:', error);
            setError(error.response?.data?.message || 'Error fetching conversion rate');
        }
    };

    const handleBillAmountChange = (e) => {
        setBillAmount(e.target.value);
    };

    const calculateAdaAmount = () => {
        if (!conversionRate || !billAmount) return 0;
        return (parseFloat(billAmount) / conversionRate).toFixed(2);
    };

    const totalPayableAmount = () => {
        const adaAmount = parseFloat(calculateAdaAmount());
        return (adaAmount + 5).toFixed(2);
    };

    return (
        <div style={styles.container}>
            <h1>USD - ADA - USD</h1>
            <h2>USD to ADA: 1$ : {conversionRate ? `${(1 / conversionRate).toFixed(2)} ADA` : 'Fetching...'}</h2>
            <h2>ADA to USD: 1 ADA : {conversionRate ? `${conversionRate.toFixed(2)} $` : 'Fetching...'}</h2>

            <div style={styles.billInputContainer}>
                <h1>Input Bill Amount</h1>
                <input 
                    type='text' 
                    placeholder='Enter your bill in USD' 
                    value={billAmount}
                    onChange={handleBillAmountChange}
                    style={styles.input}
                    required 
                />
            </div>

            <h3>Bill Amount in ADA: {calculateAdaAmount()} ADA</h3>
            <h4>Payment Processing ADA Blocked: 5 ADA</h4>
            <h3>Total Payable Amount: {calculateAdaAmount()} ADA + 5 ADA = {totalPayableAmount()} ADA</h3>

            <div style={styles.buttonContainer}>
                <button style={styles.button}>Approve Payment</button>
                <button style={styles.button}>Reject Payment</button>
            </div>

            <h3>Next Page</h3>
            <div style={styles.walletInputContainer}>
                <input
                    type="text"
                    value={walletAddress}
                    onChange={(e) => setWalletAddress(e.target.value)}
                    placeholder="Enter Wallet Address to connect eternal wallet"
                    style={styles.input}
                />
                <button onClick={fetchBalance} style={styles.button}>Connect</button>
            </div>
            {balance !== null && <p>Balance: {balance} ADA</p>}
            {error && <p style={styles.error}>{error}</p>}

            <button onClick={fetchConversionRate} style={styles.button}>Get ADA to USD Rate</button>
            {conversionRate !== null && <p>1 ADA = {conversionRate} USD</p>}

            <h3>Wallet Balance: {balance !== null ? balance : 'Fetching...'} ADA</h3>
            <button style={styles.button}>Pay 500 ADA</button>
        </div>
    );
};

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
    },
    billInputContainer: {
        margin: '20px 0',
    },
    walletInputContainer: {
        margin: '20px 0',
        display: 'flex',
        alignItems: 'center',
    },
    input: {
        padding: '10px',
        margin: '10px 0',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '300px',
    },
    buttonContainer: {
        display: 'flex',
        gap: '10px',
    },
    button: {
        padding: '10px 20px',
        margin: '10px 0',
        borderRadius: '5px',
        border: 'none',
        backgroundColor: '#007bff',
        color: '#fff',
        cursor: 'pointer',
    },
    error: {
        color: 'red',
    }
};

export default App;
