// frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
    const [walletAddress, setWalletAddress] = useState('');
    const [balance, setBalance] = useState(null);
    const [conversionRate, setConversionRate] = useState(null);
    const [error, setError] = useState(null);

    const fetchBalance = async () => {
        try {
            setError(null);
            const response = await axios.get(`http://localhost:4000/api/ada/balance/${walletAddress}`);
            setBalance(response.data.balance);
        } catch (error) {
            console.error('Error fetching balance:', error);
            setError(error.response?.data?.message || 'Error fetching balance');
        }
    };

    const fetchConversionRate = async () => {
        try {
            setError(null);
            const response = await axios.get('http://localhost:4000/api/ada/convert');
            setConversionRate(response.data.adaToUsdRate);
        } catch (error) {
            console.error('Error fetching conversion rate:', error);
            setError(error.response?.data?.message || 'Error fetching conversion rate');
        }
    };

    return (
        <div>
            <h1>USD - ADA - USD </h1>
            <h2>USD to ADA: 1$ : xxxxADA</h2>
            <h2>ADA to USD:  1ADA: xxxx$</h2>
            <input
                type="text"
                value={walletAddress}
                onChange={(e) => setWalletAddress(e.target.value)}
                placeholder="Enter Wallet Address"
            />
            <button onClick={fetchBalance}>Get Balance</button>
            {balance !== null && <p>Balance: {balance} ADA</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <button onClick={fetchConversionRate}>Get ADA to USD Rate</button>
            {conversionRate !== null && <p>1 ADA = {conversionRate} USD</p>}
        </div>
    );
};

export default App;
